import Image from 'next/image'

async function VideoComponent({
  title,
  imageSrc,
}: {
  fileName: string
  title: string
  imageSrc?: string
}) {
  // Use static image instead of video
  const fallbackImage = imageSrc || '/images/project/placeholder.png'

  return (
    <Image
      src={fallbackImage}
      alt={`${title} project preview`}
      width={800}
      height={600}
      className="size-full rounded-lg object-cover transition-all duration-300 hover:scale-105"
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
    />
  )
}

export default VideoComponent
